import{_ as t,c as i,o as e,ah as l}from"./chunks/framework.Cz7uxNdR.js";const k=JSON.parse('{"title":"Модпаки","description":"Создание, импорт и экспорт модпаков в Stuzhik. Поддержка форматов .stzhk, .mrpack (Modrinth), .zip (CurseForge). Синхронизация с друзьями.","frontmatter":{"description":"Создание, импорт и экспорт модпаков в Stuzhik. Поддержка форматов .stzhk, .mrpack (Modrinth), .zip (CurseForge). Синхронизация с друзьями.","head":[["meta",{"name":"keywords","content":"minecraft модпаки, stuzhik, стужик, создание модпака, экспорт модпака, mrpack, curseforge модпак, синхронизация модов"}]]},"headers":[],"relativePath":"guide/modpacks.md","filePath":"guide/modpacks.md"}'),o={name:"guide/modpacks.md"};function r(h,a,d,u,s,n){return e(),i("div",null,[...a[0]||(a[0]=[l('<h1 id="модпаки" tabindex="-1">Модпаки <a class="header-anchor" href="#модпаки" aria-label="Permalink to &quot;Модпаки&quot;">​</a></h1><h2 id="формат-stuzhik-stzhk" tabindex="-1">Формат Stuzhik (.stzhk) <a class="header-anchor" href="#формат-stuzhik-stzhk" aria-label="Permalink to &quot;Формат Stuzhik (.stzhk)&quot;">​</a></h2><p>Stuzhik использует собственный формат модпаков <code>.stzhk</code> - оптимизированный для быстрой установки и максимальной совместимости.</p><h3 id="преимущества" tabindex="-1">Преимущества <a class="header-anchor" href="#преимущества" aria-label="Permalink to &quot;Преимущества&quot;">​</a></h3><ul><li>Быстрая установка - оптимизированная структура</li><li>Полная совместимость - все настройки сохраняются</li><li>Компактный размер - эффективное сжатие</li><li>Метаданные - версия, автор, описание, скриншоты</li></ul><h3 id="экспорт-в-stzhk" tabindex="-1">Экспорт в .stzhk <a class="header-anchor" href="#экспорт-в-stzhk" aria-label="Permalink to &quot;Экспорт в .stzhk&quot;">​</a></h3><ol><li>Открой экземпляр</li><li>Кнопка &quot;Экспорт&quot; → &quot;Stuzhik (.stzhk)&quot;</li><li>Заполни метаданные</li><li>Сохрани файл</li></ol><h3 id="импорт-stzhk" tabindex="-1">Импорт .stzhk <a class="header-anchor" href="#импорт-stzhk" aria-label="Permalink to &quot;Импорт .stzhk&quot;">​</a></h3><p>Просто перетащи <code>.stzhk</code> файл в окно Stuzhik или:</p><ol><li>Главная → &quot;Импорт&quot;</li><li>Выбери файл</li><li>Готово</li></ol><h2 id="совместимость-с-другими-форматами" tabindex="-1">Совместимость с другими форматами <a class="header-anchor" href="#совместимость-с-другими-форматами" aria-label="Permalink to &quot;Совместимость с другими форматами&quot;">​</a></h2><p>Stuzhik также поддерживает импорт из популярных форматов:</p><table tabindex="0"><thead><tr><th>Формат</th><th>Источник</th><th>Поддержка</th></tr></thead><tbody><tr><td><code>.stzhk</code></td><td>Stuzhik</td><td>Полная</td></tr><tr><td><code>.mrpack</code></td><td>Modrinth</td><td>Полная</td></tr><tr><td><code>.zip</code></td><td>CurseForge</td><td>Полная</td></tr></tbody></table><h3 id="импорт-из-modrinth" tabindex="-1">Импорт из Modrinth <a class="header-anchor" href="#импорт-из-modrinth" aria-label="Permalink to &quot;Импорт из Modrinth&quot;">​</a></h3><ol><li>Главная → &quot;Модпак&quot;</li><li>Поиск по названию</li><li>Выбери версию → &quot;Установить&quot;</li></ol><h3 id="импорт-из-curseforge" tabindex="-1">Импорт из CurseForge <a class="header-anchor" href="#импорт-из-curseforge" aria-label="Permalink to &quot;Импорт из CurseForge&quot;">​</a></h3><ol><li>Скачай <code>.zip</code> с CurseForge</li><li>Stuzhik → &quot;Импорт&quot;</li><li>Выбери файл</li></ol><h2 id="создание-модпака" tabindex="-1">Создание модпака <a class="header-anchor" href="#создание-модпака" aria-label="Permalink to &quot;Создание модпака&quot;">​</a></h2><h3 id="что-включается" tabindex="-1">Что включается <a class="header-anchor" href="#что-включается" aria-label="Permalink to &quot;Что включается&quot;">​</a></h3><ul><li>Список модов с версиями</li><li>Конфигурации модов</li><li>Настройки игры</li><li>Ресурспаки и шейдеры (опционально)</li></ul><h3 id="что-не-включается" tabindex="-1">Что НЕ включается <a class="header-anchor" href="#что-не-включается" aria-label="Permalink to &quot;Что НЕ включается&quot;">​</a></h3><ul><li>Minecraft (скачивается отдельно)</li><li>Загрузчик (устанавливается автоматически)</li><li>Сохранения миров</li></ul><h2 id="редактор-модпаков" tabindex="-1">Редактор модпаков <a class="header-anchor" href="#редактор-модпаков" aria-label="Permalink to &quot;Редактор модпаков&quot;">​</a></h2><h3 id="сравнение-версии" tabindex="-1">Сравнение версий <a class="header-anchor" href="#сравнение-версии" aria-label="Permalink to &quot;Сравнение версий&quot;">​</a></h3><ol><li>Модпак → &quot;Редактор&quot;</li><li>Загрузи две версии</li><li>Увидишь: <ul><li>Добавленные моды</li><li>Удалённые моды</li><li>Обновлённые моды</li></ul></li></ol><h3 id="обновление" tabindex="-1">Обновление <a class="header-anchor" href="#обновление" aria-label="Permalink to &quot;Обновление&quot;">​</a></h3><ol><li>Открой экземпляр с модпаком</li><li>&quot;Проверить обновления&quot;</li><li>Применить изменения</li></ol><h2 id="серверные-модпаки" tabindex="-1">Серверные модпаки <a class="header-anchor" href="#серверные-модпаки" aria-label="Permalink to &quot;Серверные модпаки&quot;">​</a></h2><p>При экспорте для сервера Stuzhik автоматически:</p><ul><li>Исключает client-only моды</li><li>Добавляет server-only моды</li><li>Настраивает <code>server.properties</code></li></ul><h2 id="синхронизация-с-друзьями" tabindex="-1">Синхронизация с друзьями <a class="header-anchor" href="#синхронизация-с-друзьями" aria-label="Permalink to &quot;Синхронизация с друзьями&quot;">​</a></h2><ol><li>Экспортируй модпак в <code>.stzhk</code></li><li>Отправь друзьям</li><li>Они импортируют в Stuzhik</li><li>Все на одинаковых версиях</li></ol><h2 id="решение-проблем" tabindex="-1">Решение проблем <a class="header-anchor" href="#решение-проблем" aria-label="Permalink to &quot;Решение проблем&quot;">​</a></h2><h3 id="установка-зависла" tabindex="-1">Установка зависла <a class="header-anchor" href="#установка-зависла" aria-label="Permalink to &quot;Установка зависла&quot;">​</a></h3><p>Dev Console (<code>Ctrl+Shift+D</code>) покажет детали. Возможные причины:</p><ul><li>Медленное соединение</li><li>Недоступный мод</li></ul><h3 id="краш-после-установки" tabindex="-1">Краш после установки <a class="header-anchor" href="#краш-после-установки" aria-label="Permalink to &quot;Краш после установки&quot;">​</a></h3><p>Log Analyzer автоматически найдёт проблемные моды и предложит решения.</p>',38)])])}const q=t(o,[["render",r]]);export{k as __pageData,q as default};
