import{_ as l,c as i,o as t,ah as o}from"./chunks/framework.tk4zCJ0Z.js";const b=JSON.parse('{"title":"Управление модами","description":"","frontmatter":{},"headers":[],"relativePath":"guide/mods.md","filePath":"guide/mods.md"}'),e={name:"guide/mods.md"};function r(n,a,d,h,s,u){return t(),i("div",null,[...a[0]||(a[0]=[o(`<h1 id="управление-модами" tabindex="-1">Управление модами <a class="header-anchor" href="#управление-модами" aria-label="Permalink to &quot;Управление модами&quot;">​</a></h1><h2 id="установка-модов" tabindex="-1">Установка модов <a class="header-anchor" href="#установка-модов" aria-label="Permalink to &quot;Установка модов&quot;">​</a></h2><h3 id="из-каталога" tabindex="-1">Из каталога <a class="header-anchor" href="#из-каталога" aria-label="Permalink to &quot;Из каталога&quot;">​</a></h3><ol><li>Открой экземпляр</li><li>Вкладка &quot;Моды&quot; → &quot;Каталог&quot;</li><li>Найди мод через поиск</li><li>Кнопка &quot;Установить&quot;</li></ol><p>Stuzhik автоматически:</p><ul><li>Выберет версию для твоей версии Minecraft</li><li>Проверит совместимость с загрузчиком</li><li>Предупредит о конфликтах</li><li>Установит зависимости</li></ul><h3 id="из-фаила" tabindex="-1">Из файла <a class="header-anchor" href="#из-фаила" aria-label="Permalink to &quot;Из файла&quot;">​</a></h3><p>Перетащи <code>.jar</code> файл в окно или:</p><ol><li>Кнопка &quot;Добавить локальный мод&quot;</li><li>Выбери <code>.jar</code> файл</li><li>Готово</li></ol><h3 id="зависимости" tabindex="-1">Зависимости <a class="header-anchor" href="#зависимости" aria-label="Permalink to &quot;Зависимости&quot;">​</a></h3><p>Если мод требует другие моды:</p><ol><li>Stuzhik покажет список зависимостей</li><li>Кнопка &quot;Установить все&quot;</li><li>Все зависимости установятся автоматически</li></ol><h2 id="управление-модами-1" tabindex="-1">Управление модами <a class="header-anchor" href="#управление-модами-1" aria-label="Permalink to &quot;Управление модами&quot;">​</a></h2><h3 id="включение-отключение" tabindex="-1">Включение/отключение <a class="header-anchor" href="#включение-отключение" aria-label="Permalink to &quot;Включение/отключение&quot;">​</a></h3><ol><li>Список модов → клик на переключатель</li><li>Мод переименуется в <code>.jar.disabled</code></li><li>При следующем запуске не загрузится</li></ol><h3 id="обновление" tabindex="-1">Обновление <a class="header-anchor" href="#обновление" aria-label="Permalink to &quot;Обновление&quot;">​</a></h3><p><strong>Вручную:</strong></p><ol><li>Клик на мод → &quot;Обновить&quot;</li><li>Stuzhik найдёт последнюю версию</li><li>Заменит файл</li></ol><p><strong>Автоматически:</strong></p><ol><li>Клик на мод → &quot;Настройки&quot;</li><li>Включи &quot;Автообновление&quot;</li><li>При запуске проверяется новая версия</li></ol><h3 id="удаление" tabindex="-1">Удаление <a class="header-anchor" href="#удаление" aria-label="Permalink to &quot;Удаление&quot;">​</a></h3><ol><li>Клик на мод → &quot;Удалить&quot;</li><li>Или выбери несколько → &quot;Удалить выбранные&quot;</li></ol><h2 id="поиск-модов" tabindex="-1">Поиск модов <a class="header-anchor" href="#поиск-модов" aria-label="Permalink to &quot;Поиск модов&quot;">​</a></h2><h3 id="фильтры" tabindex="-1">Фильтры <a class="header-anchor" href="#фильтры" aria-label="Permalink to &quot;Фильтры&quot;">​</a></h3><ul><li><strong>Категория:</strong> Tech, Magic, Decoration, и т.д.</li><li><strong>Загрузчик:</strong> Forge/Fabric/NeoForge/Quilt</li><li><strong>Версия MC:</strong> Только совместимые</li><li><strong>Источник:</strong> Modrinth/CurseForge</li></ul><h3 id="сортировка" tabindex="-1">Сортировка <a class="header-anchor" href="#сортировка" aria-label="Permalink to &quot;Сортировка&quot;">​</a></h3><ul><li>По популярности (downloads)</li><li>По дате обновления</li><li>По рейтингу</li></ul><h2 id="конфликты-модов" tabindex="-1">Конфликты модов <a class="header-anchor" href="#конфликты-модов" aria-label="Permalink to &quot;Конфликты модов&quot;">​</a></h2><h3 id="автоматическая-проверка" tabindex="-1">Автоматическая проверка <a class="header-anchor" href="#автоматическая-проверка" aria-label="Permalink to &quot;Автоматическая проверка&quot;">​</a></h3><p>При установке Stuzhik проверяет:</p><table tabindex="0"><thead><tr><th>Тип конфликта</th><th>Пример</th></tr></thead><tbody><tr><td>Несовместимость</td><td>OptiFine + Sodium</td></tr><tr><td>Дубликаты</td><td>Sodium + Rubidium</td></tr><tr><td>Loader mismatch</td><td>Forge мод на Fabric</td></tr><tr><td>Версия MC</td><td>Мод для 1.19 на 1.20</td></tr></tbody></table><h3 id="решение-конфликтов" tabindex="-1">Решение конфликтов <a class="header-anchor" href="#решение-конфликтов" aria-label="Permalink to &quot;Решение конфликтов&quot;">​</a></h3><p>Если конфликт найден:</p><ol><li><strong>Critical</strong> - установка заблокирована</li><li><strong>Warning</strong> - можно установить, но будет краш</li><li><strong>Info</strong> - работать будет, но могут быть баги</li></ol><p>Stuzhik предложит:</p><ul><li>Удалить конфликтующий мод</li><li>Установить альтернативу</li><li>Игнорировать (на свой риск)</li></ul><h2 id="рекомендации" tabindex="-1">Рекомендации <a class="header-anchor" href="#рекомендации" aria-label="Permalink to &quot;Рекомендации&quot;">​</a></h2><h3 id="на-основе-установленных" tabindex="-1">На основе установленных <a class="header-anchor" href="#на-основе-установленных" aria-label="Permalink to &quot;На основе установленных&quot;">​</a></h3><p>Stuzhik анализирует:</p><ul><li>Категории установленных модов</li><li>Популярные комбинации</li><li>Addon&#39;ы для модов</li></ul><p>И предлагает моды которые:</p><ul><li>Дополняют установленные</li><li>Популярны вместе с ними</li><li>Являются зависимостями других</li></ul><h3 id="trending" tabindex="-1">Trending <a class="header-anchor" href="#trending" aria-label="Permalink to &quot;Trending&quot;">​</a></h3><p>Показываются популярные моды для:</p><ul><li>Твоей версии Minecraft</li><li>Твоего загрузчика</li><li>Категорий которые уже установлены</li></ul><h2 id="производительность" tabindex="-1">Производительность <a class="header-anchor" href="#производительность" aria-label="Permalink to &quot;Производительность&quot;">​</a></h2><h3 id="оптимизация" tabindex="-1">Оптимизация <a class="header-anchor" href="#оптимизация" aria-label="Permalink to &quot;Оптимизация&quot;">​</a></h3><p>Моды которые улучшают FPS:</p><p><strong>Forge:</strong></p><ul><li>Optifine (альтернатива: Embeddium + Oculus)</li><li>EntityCulling</li><li>FpsReducer</li></ul><p><strong>Fabric:</strong></p><ul><li>Sodium</li><li>Lithium</li><li>Phosphor</li><li>Entity Culling</li></ul><h3 id="мониторинг" tabindex="-1">Мониторинг <a class="header-anchor" href="#мониторинг" aria-label="Permalink to &quot;Мониторинг&quot;">​</a></h3><p>Stuzhik показывает:</p><ul><li>Размер модов на диске</li><li>Влияние на время загрузки</li><li>Подозрительные моды при крашах</li></ul><h2 id="где-хранятся-моды" tabindex="-1">Где хранятся моды <a class="header-anchor" href="#где-хранятся-моды" aria-label="Permalink to &quot;Где хранятся моды&quot;">​</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span>Windows: %APPDATA%\\.minecraft-launcher\\instances\\&lt;id&gt;\\mods\\</span></span>
<span class="line"><span>Linux:   ~/.minecraft-launcher/instances/&lt;id&gt;/mods/</span></span>
<span class="line"><span>macOS:   ~/Library/Application Support/minecraft-launcher/instances/&lt;id&gt;/mods/</span></span></code></pre></div><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="мод-не-появляется-в-игре" tabindex="-1">Мод не появляется в игре <a class="header-anchor" href="#мод-не-появляется-в-игре" aria-label="Permalink to &quot;Мод не появляется в игре&quot;">​</a></h3><p><strong>Проверь:</strong></p><ol><li>Правильный загрузчик? (Forge мод на Forge)</li><li>Правильная версия MC?</li><li>Мод включён? (не <code>.disabled</code>)</li><li>Есть зависимости? (Fabric API для Fabric)</li></ol><h3 id="краш-после-установки-мода" tabindex="-1">Краш после установки мода <a class="header-anchor" href="#краш-после-установки-мода" aria-label="Permalink to &quot;Краш после установки мода&quot;">​</a></h3><ol><li>Log Analyzer автоматически найдёт виновный мод</li><li>Удали мод через Stuzhik</li><li>Или откати версию: клик на мод → &quot;Откатить&quot;</li></ol><h3 id="мод-не-обновляется" tabindex="-1">Мод не обновляется <a class="header-anchor" href="#мод-не-обновляется" aria-label="Permalink to &quot;Мод не обновляется&quot;">​</a></h3><ol><li>Проверь что автообновление включено</li><li>Может быть новая версия только для новой MC</li><li>Dev Console (<code>Ctrl+Shift+D</code>) → есть ошибки?</li></ol>`,65)])])}const q=l(e,[["render",r]]);export{b as __pageData,q as default};
